package project1;

public class FourthProblem {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int anyNumber=0;
		if(anyNumber!=0) {
			if(anyNumber==(anyNumber/2)+(anyNumber/2)) {
				System.out.println("The given number is Even");
			}
			else if(anyNumber!=(anyNumber/2)+(anyNumber/2)) {
				System.out.println("The given number is odd");
			}
		}
		else {
			System.out.println("The given number is nither Even nor Odd that is,it is Zero");
		}
	}
}
